 

public class all_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 50, b = 25, sum, difference, product, division, modulus;
		sum = a + b;
		difference = a - b;
		product = a * b;
		division = a / b;
		modulus = a % b;
		System.out.println("The sum of " +a+ " and " +b+ " is " +sum+ ".");
		System.out.println("The difference of " +b+ " from " +a+ " is " +difference+ ".");
		System.out.println("The product of " +a+ " and " +b+ " is " +product+ ".");
		System.out.println("The division of " +a+ " by " +b+ " is " +division+ ".");
		System.out.println("The modulus of " +a+ " by " +b+ " is " +modulus+ ".");

	}

}

